import { menuData } from "@/assets/js/data/menu";
import router from "@/router";

export default {
  state: {
    // 动态菜单
    menuData: [],
  },
  getters: {},
  //修改字段
  mutations: {
    //设置菜单的数据
    setMenu(state, val) {
      state.menuData = val;
    },
    //动态注册路由
    addMenu(state) {
      // 处理动态路由的数据
      const menuData = JSON.parse(JSON.stringify(state.menuData));
      console.log(menuData);
      const menuArray = [];
      menuData.forEach((item) => {
        menuArray.push(item);
      });
      // 路由的动态添加
      if (menuArray[0] !== "") {
        menuArray.forEach((item) => {
          var path = item.path.slice(1); // 去除'/';
          var component =
          router.addRoute("main", {
            path: `${path}`,
            name: `${path}`,
            component: () => import(`@/views/${component}.vue`),
          });
        });
        console.log(menuArray, "处理之后的菜单");
      }
    },
  },
  actions: {},
  modules: {},
};
