import router from "@/router";
import { getComponent } from "@/assets/js/utils/add-menu";

export default {
  state: {
    // 动态菜单
    menuData: [],
  },
  getters: {},
  //修改字段
  mutations: {
    //设置菜单的数据
    setMenu(state, val) {
      state.menuData = val;
      addMenu(val);
    },
  },
  actions: {},
  modules: {},
};
